    private void squareActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_squareActionPerformed
        // TODO add your handling code here:
        num1 = Double.valueOf(txtVal.getText());
        double power = Math.pow(num1,2);
        txtVal.setText(String.valueOf(power));
    }//GEN-LAST:event_squareActionPerformed

    private void butEqActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_butEqActionPerformed
        // TODO add your handling code here:
        num2 = Double.valueOf(txtVal.getText());
        if(action == "+"){          //if("+".equals(action)
            result = (num1 + num2);
        }
        else if(action == "-"){
            result = (num1 - num2);
        }
        else if(action == "*"){
            result = (num1 * num2);
        }
        else if(action == "/"){
            result = (num1 / num2);
        }
        txtVal.setText(String.valueOf(result));

    }//GEN-LAST:event_butEqActionPerformed

